mapper.select:
SELECT u.*,
FROM cx_ar_user_ot_evaluates u left join cx_ar_user aruser  on  u.user_id=aruser.id

    u.status = 1

        and u.ot_tattoo_id = #{otTattooId}


        and u.type = #{type}


        and u.level = #{level}


        and u.reply_id = #{replyId}


order by u.great_num desc

SELECT u.*,
FROM cx_ar_user_ot_evaluates u left join cx_ar_user aruser  on  u.user_id=aruser.id

    u.status = 1

        and u.ot_tattoo_id = #{otTattooId}


        and u.type = #{type}


        and u.level = #{level}


        and u.reply_id = #{replyId}


        and u.id = #{otEvaluateId}


order by u.great_num desc limit 1
